	public function index($balance_name = '')
	{
		// $this->session->set_userdata('access_token', 'test');
		if(!$balance_name) {
			$balance_name = $this->session->userdata('gallywix_name');
		}

		$balance_name = urldecode($balance_name);

		if(!$this->session->userdata('access_token') || !$balance_name) {
			redirect('balance/login');
		}

		if($this->session->userdata('region')) {
			$region = $this->session->userdata('region');
		}

		if($this->input->post("cycleView")) {
			$cycle = explode('/', $this->input->post("cycleView"));
			$cycleStart = $cycle[0];
			$cycleEnd = $cycle[1];
			if($region == "EU") {
				$cycles = $this->config->item('eu_cycles');
			} else {
				$cycles = $this->config->item('na_cycles');
			}
		} else {
			if($region == "EU") {
				$cycleStart = $this->config->item('eu_cycle_start');
				$cycleEnd = $this->config->item('eu_cycle_end');
				$cycles = $this->config->item('eu_cycles');
			} else {
				$cycleStart = $this->config->item('na_cycle_start');
				$cycleEnd = $this->config->item('na_cycle_end');
				$cycles = $this->config->item('na_cycles');
			}
		}

		$data['balance'] = $this->db->where('name', $balance_name)->get('balance')->row();
		$data['old_balance'] = $this->db->where('name', $balance_name)->get('old_balance')->row();
		$data['mplus'] = $this->db
											->where('(advertiser = "'.$balance_name.'"
											OR booster_1  = "'.$balance_name.'"
											OR booster_2  = "'.$balance_name.'"
											OR booster_3  = "'.$balance_name.'"
											OR booster_4  = "'.$balance_name.'")')
											->where('region', $region)
											->where("DATE(CONCAT('2020','-',TRIM(SUBSTRING_INDEX(date,'/',-1)),'-',TRIM(SUBSTRING_INDEX(date,'/', 1)))) BETWEEN '".$cycleStart."' AND '".$cycleEnd."'")
											->get('mythic_plus')->result_array();
		$data['levelling'] = $this->db
											->where('(advertiser = "'.$balance_name.'" OR booster_1  = "'.$balance_name.'" OR booster_2  = "'.$balance_name.'")')
											->where('region', $region)
											->where("DATE(CONCAT('2020','-',TRIM(SUBSTRING_INDEX(date,'/',-1)),'-',TRIM(SUBSTRING_INDEX(date,'/', 1)))) BETWEEN '".$cycleStart."' AND '".$cycleEnd."'")
											->get('levelling')->result_array();
		$data['pvp'] = $this->db
											->where('(advertiser = "'.$balance_name.'" OR booster_1  = "'.$balance_name.'" OR booster_2  = "'.$balance_name.'")')
											->where('region', $region)
											->where("DATE(CONCAT('2020','-',TRIM(SUBSTRING_INDEX(date,'/',-1)),'-',TRIM(SUBSTRING_INDEX(date,'/', 1)))) BETWEEN '".$cycleStart."' AND '".$cycleEnd."'")
											->get('pvp')->result_array();
		$data['raids'] = $this->db
											->where("(FIND_IN_SET(\"{$balance_name}\", boosters) OR gc = \"".$balance_name."\")")
											->where("DATE(CONCAT('2020','-',TRIM(SUBSTRING_INDEX(date,'/',-1)),'-',TRIM(SUBSTRING_INDEX(date,'/', 1)))) BETWEEN '".$cycleStart."' AND '".$cycleEnd."'")
											->where('region', $region)
											->get('raids')->result_array();
		$data['raids'] = array_unique($data['raids'], SORT_REGULAR);
		$data['manual_edits'] = $this->db
											->where('name', $balance_name)
											->where("DATE(CONCAT('2020','-',TRIM(SUBSTRING_INDEX(date,'/',-1)),'-',TRIM(SUBSTRING_INDEX(date,'/', 1)))) BETWEEN '".$cycleStart."' AND '".$cycleEnd."'")
											->where('region', $region)
											->get('manual_edits')->result_array();
		$data['gambling'] = $this->db
											->where('name', $balance_name)
											->where("date BETWEEN '".$cycleStart."' AND '".$cycleEnd."'")
											->where('region', $region)
											->get('gambling')->result_array();

		$data['total'] = 0;
		foreach($data['manual_edits'] as $m) {
			@$data['total'] += floatval(str_replace(',', '', $m['amount']));
		}
		foreach($data['gambling'] as $m) {
			@$data['total'] += floatval(str_replace(',', '', $m['amount']));
		}
		foreach($data['raids'] as $m) {
			if(strtolower($m['gc']) == strtolower($balance_name)) {
				@$data['total'] += floatval(str_replace(',', '', $m['gc_total']));
			} else {
				@$data['total'] += floatval(str_replace(',', '', $m['booster_cut']));
			}
		}
		foreach($data['levelling'] as $m) {
			if(strtolower($balance_name) == strtolower($m['booster_1'])) {
				@$data['total'] += floatval(str_replace(',', '', $m['booster_cut']));
			}
			if(strtolower($balance_name) == strtolower($m['booster_2'])) {
				@$data['total'] += floatval(str_replace(',', '', $m['booster_cut']));
			}
			if(strtolower($balance_name) == strtolower($m['advertiser'])) {
				@$data['total'] += floatval(str_replace(',', '', $m['ad_cut']));
			}
		}
		foreach($data['pvp'] as $m) {
			if(strtolower($balance_name) == strtolower($m['advertiser'])) {
				@$data['total'] += floatval(str_replace(',', '', $m['ad_cut']));
			}
			if(strtolower($balance_name) == strtolower($m['booster_1'])) {
				@$data['total'] += floatval(str_replace(',', '', $m['booster_cut']));
			}
			if(strtolower($balance_name) == strtolower($m['booster_2'])) {
				@$data['total'] += floatval(str_replace(',', '', $m['booster_cut']));
			}
		}
		foreach($data['mplus'] as $m) {
			if(strtolower($balance_name) == strtolower($m['advertiser'])) {
				@$data['total'] += floatval(str_replace(',', '', $m['ad_cut']));
			}
			if(strtolower($balance_name) == strtolower($m['booster_1'])) {
				@$data['total'] += floatval(str_replace(',', '', $m['booster_cut']));
			}
			if(strtolower($balance_name) == strtolower($m['booster_2'])) {
				@$data['total'] += floatval(str_replace(',', '', $m['ad_cut']));
			}
			if(strtolower($balance_name) == strtolower($m['booster_3'])) {
				@$data['total'] += floatval(str_replace(',', '', $m['booster_cut']));
			}
			if(strtolower($balance_name) == strtolower($m['booster_4'])) {
				@$data['total'] += floatval(str_replace(',', '', $m['booster_cut']));
			}
		}
		$data['cycleStart'] = $cycleStart;
		$data['cycleEnd'] = $cycleEnd;
		$data['cycles'] = $cycles;
		$data['balance_name'] = $balance_name;
		$this->load->view('balance_page', $data);
	}

