				} elseif ($gallywixName == "Bogi") {
					$gallywixName = "Rollingmonk-Kazzak";
				} elseif ($gallywixName == "Kit") {
					$gallywixName = "Kit-TarrenMill";
				} elseif ($gallywixName == "Daddy Dalom") {
					$gallywixName = "Dalom-TarrenMill";
				} elseif ($gallywixName == "Jzen") {
					$gallywixName = "Jzen-TarrenMill";
				}

				// advertiser eu: 461839226581942276
				// advertiser na: 556622546351423498
				// booster eu: 443759760534142976
				// booster na: 556607029939011604
				// council: 443520205826818058
				// staff: 692508840977105028
				$allowedRoles = array("467495325381296131", "443520205826818058", "461839226581942276", "443759760534142976", "556622546351423498", "556607029939011604", "692508840977105028");
				$naRoles = array('556607029939011604', '556622546351423498');
				$allowedIn = !empty(array_intersect($allowedRoles, $gallywix_discord_match->roles));

				if(!empty(array_intersect($naRoles, $gallywix_discord_match->roles))) {
